from crf_hbpdrxxjl
where hbphzxxid = #{hbphzxxid}

select hbphzxx.hbphzxxid ${sqlParam}
from (

    select #{item,jdbcType=VARCHAR} hbphzxxid,#{index} ordernum

) tmp
left outer join crf_hbphzxx hbphzxx on hbphzxx.hbphzxxid = tmp.hbphzxxid
