mapper.select:
select xcg.qmngsndzjlid,xcg.jyxm,xcg.sjz,sj.csmc,sj.csid,sj.csdm,sj.cskz1,sj.cskz2 from matridx_jcsj sj left outer join crf_qmngsxcg xcg on sj.csid =xcg.jyxm and xcg.qmngsndzjlid = #{qmngsndzjlid}
sj.jclb='QMNGSBLOODTEST' and sj.scbj!='1'
order by sj.cspx,sj.csid

select dmxq.qmngsndzjlid,dmxq.jyxm,dmxq.sjz,sj.csmc,sj.csid,sj.csdm,sj.cskz1,sj.cskz2,jl.jldjt from crf_qmngsxcg dmxq left join matridx_jcsj sj on sj.csid =dmxq.jyxm left join crf_qmngsndzxxjl jl on jl.qmngsndzjlid=dmxq.qmngsndzjlid
sj.scbj!='1' and jl.qmngshzid=#{qmngshzid}
order by sj.cspx
