mapper.delete:
	delete from matridx_dsrwqx


        	and rwid in

				#{items}



        	and ((yhid in

				#{item}
			)
			or (jsid in

				#{item}

			))

mapper.insert:
insert into matridx_dsrwqx (yhid, rwid,lrry,lrsj,jsid)


		select #{item},ds.rwid,#{lrry},LOCALTIMESTAMP,''
		from matridx_dsrwsz ds


				and ds.rwid in

					#{items}







		select '',ds.rwid,#{lrry},LOCALTIMESTAMP,#{item}
		from matridx_dsrwsz ds


				and ds.rwid in

					#{items}
