mapper.select:
SELECT
    qzsz.qzszid,qzsz.csszid,qzsz.jxmbid,qzsz.gwid,qzsz.qz,qzsz.mbszid,spgw.gwmc,qzsz.xh
FROM
igams_qzsz qzsz
left join matridx_spgw spgw on spgw.gwid = qzsz.gwid

1=1

        and qzsz.csszid = #{csszid}


        and qzsz.jxmbid = #{jxmbid}


        and qzsz.mbszid = #{mbszid}


ORDER BY qzsz.xh ASC
