mapper.select:
select xzllcgl.ryid,xzllcgl.xzkcid,xzkcxx.hwmc,xzkcxx.hwbz,xzkcxx.kcl,(coalesce(xzkcxx.kcl,0) - coalesce(xzkcxx.yds,0)) klsl,coalesce(xzkcxx.yds,0) yds, jcsj.csmc kw from igams_xzllcgl xzllcgl left join igams_xzkcxx xzkcxx on xzkcxx.xzkcid=xzllcgl.xzkcid left join matridx_jcsj jcsj on xzkcxx.kwid = jcsj.csid
xzllcgl.ryid=#{ryid}
group by xzkcxx.yds,xzkcxx.kcl, xzkcxx.hwmc,xzkcxx.hwbz,xzllcgl.ryid,xzllcgl.xzkcid,jcsj.csmc

mapper.delete:
delete from igams_xzllcgl

    ryid = #{ryid}

    and xzkcid = #{xzkcid}


     and xzkcid in

         #{item}
