mapper.select:
select qx.yhid,qx.jsid,js.jsmc,js.fjsid,js.sylx,js.jsdm,yh.yhm yhm,yh.zsxm zsxm,js.dwxdbj,ssjg.jgid,js.ckqx from matridx_yhjs qx left outer join matridx_xtjs js on qx.jsid = js.jsid left outer join matridx_xtyh yh on yh.yhid = qx.yhid left outer join matridx_yhssjg ssjg on ssjg.yhid = yh.yhid and ssjg.xh ='1'
js.scbj ='0'
and qx.yhid =#{yhid}
and qx.jsid =#{jsid}
order by js.jsdm,js.jsid

select yh.yhid,yh.yhm,yh.zsxm from matridx_yhjs yhjs left outer join matridx_xtyh yh on yh.yhid = yhjs.yhid
yh.scbj='0' and yh.sfsd='0' and yhjs.jsid = #{jsid}
and yh.yhid = #{yhid}
and yh.yhm like '%'||#{yhm}||'%'
and yh.zsxm like '%'||#{zsxm}||'%'

select yh.yhid,yh.yhm,yh.zsxm from matridx_yhjs yhjs left outer join matridx_xtyh yh on yh.yhid = yhjs.yhid
yh.scbj='0' and yh.sfsd='0' and yhjs.jsid = #{jsid}
and yh.yhid = #{yhid}
and yh.yhm like '%'||#{yhm}||'%'
and yh.zsxm like '%'||#{zsxm}||'%'

mapper.delete:
	delete from matridx_yhjs


        	and yhid = #{yhid}


        	and yhid in

				#{item}



        	and jsid = #{jsid}

mapper.insert:
		insert into matridx_yhjs (yhid,  jsid)

        	values (#{yhid},#{jsid})



				select #{item},#{jsid}

