mapper.update:
update igams_jcsqmx

    scbj='1',
    scsj=LOCALTIMESTAMP,
    scry=#{scry}



        sqglid=#{sqglid}


        sqglid in

            #{item}

mapper.select:
select jcsqmx.sqmxid
from igams_jcsqmx jcsqmx
left join igams_jcsqgl jcsqgl on jcsqgl.sqglid=jcsqmx.sqglid and jcsqgl.scbj='0'

    jcsqmx.scbj='0' and jcsqgl.zt='10' and jcsqmx.sjid in

        #{item}


        and jcsqmx.sqglid!=#{sqglid}
