mapper.select:
select jscz.jsid,jscz.zyid,jscz.czdm,czb.dyym,dm.czmc,dm.xssx,dm.anys,dm.czsm from matridx_jszyczb jscz left outer join matridx_zyczb czb on czb.zyid = jscz.zyid and czb.czdm = jscz.czdm left outer join matridx_czdm dm on dm.czdm = czb.czdm where czb.czdm is not null
and jsid = #{jsid}
order by zyid,xssx

select jscz.jsid,jscz.zyid,jscz.czdm,czb.dyym,dm.czmc,dm.xssx,dm.anys,zy.gnm,dm.czsm from matridx_jszyczb jscz left outer join matridx_xtzy zy on jscz.zyid = zy.zyid left outer join matridx_zyczb czb on czb.zyid = jscz.zyid and czb.czdm = jscz.czdm left outer join matridx_czdm dm on dm.czdm = czb.czdm where czb.czdm is not null
and jsid = #{jsid}
order by zyid,xssx
